import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Search() {
    const [breed, setBreed] = useState(''); // Starting with an empty string for breed
    const [dogIds, setDogIds] = useState([]); // This will hold the IDs fetched based on the breed
    const [dogDetails, setDogDetails] = useState([]); // This will store the detailed information of the dogs
    const [loading, setLoading] = useState(false); // Track loading state
    const [error, setError] = useState(null); // Track errors

    // Fetch breeds on component mount to populate breed options
    useEffect(() => {
        axios.get('https://frontend-take-home-service.fetch.com/dogs/breeds')
            .then(response => {
                setBreeds(response.data); // Assuming response.data contains the breeds directly
            })
            .catch(error => {
                console.error('Failed to fetch breeds:', error);
                setError('Failed to fetch breeds. Please try again.');
            });
    }, []);

    // Fetch dog IDs whenever the selected breed changes
    useEffect(() => {
        if (breed) {
            setLoading(true);
            axios.get(`https://frontend-take-home-service.fetch.com/dogs/search`, {
                params: { breed: breed, size: 25, from: 0, sort: 'breed:asc' }
            })
            .then(response => {
                setDogIds(response.data.resultIds);
                setLoading(false);
            })
            .catch(error => {
                console.error(`Failed to fetch dogs for breed ${breed}:`, error);
                setError(`Failed to fetch dogs for breed ${breed}. Please try again.`);
                setLoading(false);
            });
        }
    }, [breed]);

    // Fetch detailed information for each dog when dogIds updates
    useEffect(() => {
        if (dogIds.length > 0) {
            setLoading(true);
            const detailsPromises = dogIds.map(id => 
                axios.get(`https://frontend-take-home-service.fetch.com/dogs/${id}`)
                .catch(err => {
                    console.error(`Failed to fetch details for dog ID ${id}:`, err);
                    return null; // Return null for errors to filter them out later
                })
            );

            Promise.all(detailsPromises)
            .then(details => {
                setDogDetails(details.filter(detail => detail !== null)); // Filter out failed requests
                setLoading(false);
            })
            .catch(error => {
                console.error('Failed to fetch dog details:', error);
                setError('Failed to fetch dog details. Please try again.');
                setLoading(false);
            });
        }
    }, [dogIds]);

    return (
        <div>
            <h1>Search Dogs by Breed</h1>
            <select value={breed} onChange={e => setBreed(e.target.value)}>
                <option value="">Select a breed</option>
                {breeds.map((b, index) => (
                    <option key={index} value={b}>{b}</option>
                ))}
            </select>
            {loading && <p>Loading...</p>}
            {error && <p>Error: {error}</p>}
            <ul>
                {dogDetails.map((dog, index) => (
                    <li key={index}>
                        Name: {dog.name}, Breed: {dog.breed}, Age: {dog.age}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Search;
