// Search.js
import React, { useState, useEffect } from 'react';
import { searchDogs, generateMatch, fetchDogsByIds } from '../api/fetchAPI';
import axios from 'axios';

function Search() {
  // Filter and pagination states
  const [breed, setBreed] = useState('');
  const [breeds, setBreeds] = useState([]);
  const [sortOrder, setSortOrder] = useState('asc'); // "asc" or "desc"
  const [pageFrom, setPageFrom] = useState(0); // cursor/page offset
  const pageSize = 25;

  // Data and UI states
  const [dogDetails, setDogDetails] = useState([]);
  const [total, setTotal] = useState(0);
  const [nextQuery, setNextQuery] = useState(null);
  const [prevQuery, setPrevQuery] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Favorites and match state
  const [favorites, setFavorites] = useState([]);
  const [matchResult, setMatchResult] = useState(null);
  const [matchedDog, setMatchedDog] = useState(null); // full details of matched dog

  // Inline styles for UI improvements
  const containerStyle = {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '2rem'
  };

  const headerStyle = {
    textAlign: 'center',
    marginBottom: '2rem'
  };

  const cardStyle = {
    border: '1px solid #ccc',
    borderRadius: '8px',
    padding: '1rem',
    margin: '1rem',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
    flex: '1 1 300px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center'
  };

  const cardImageStyle = {
    maxWidth: '100%',
    borderRadius: '4px',
    marginBottom: '1rem'
  };

  const cardsContainerStyle = {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center'
  };

  const buttonStyle = {
    padding: '0.5rem 1rem',
    margin: '0.5rem',
    borderRadius: '4px',
    border: 'none',
    cursor: 'pointer'
  };

  // Fetch available breeds on component mount
  useEffect(() => {
    axios.get('https://frontend-take-home-service.fetch.com/dogs/breeds')
      .then(response => {
        setBreeds(response.data);
      })
      .catch(error => {
        console.error('Failed to fetch breeds:', error);
        setError('Failed to fetch breeds. Please try again.');
      });
  }, []);

  // Fetch dogs whenever breed, pageFrom, or sortOrder changes
  useEffect(() => {
    if (breed) {
      setLoading(true);
      setError(null);
      const filters = { 
        breeds: [breed], 
        size: pageSize, 
        from: pageFrom, 
        sort: `breed:${sortOrder}` 
      };
      searchDogs(filters)
        .then(({ dogs, total, next, prev }) => {
          setDogDetails(dogs);
          setTotal(total);
          setNextQuery(next);
          setPrevQuery(prev);
          setLoading(false);
        })
        .catch(err => {
          console.error(`Failed to fetch dogs for breed ${breed}:`, err);
          setError(`Failed to fetch dogs for breed ${breed}. Please try again.`);
          setLoading(false);
        });
    } else {
      setDogDetails([]);
      setTotal(0);
    }
  }, [breed, pageFrom, sortOrder]);

  // Handle favorite toggling for a dog
  const toggleFavorite = (dogId) => {
    setFavorites(prev => 
      prev.includes(dogId) ? prev.filter(id => id !== dogId) : [...prev, dogId]
    );
  };

  // Generate match based on favorited dog IDs
  const handleGenerateMatch = () => {
    if (favorites.length === 0) {
      alert('Please select at least one favorite dog.');
      return;
    }
    generateMatch(favorites)
      .then(data => {
        setMatchResult(data.match);
        // Now fetch full details for the matched dog
        return fetchDogsByIds([data.match]);
      })
      .then(matchedDogs => {
        if (matchedDogs && matchedDogs.length > 0) {
          setMatchedDog(matchedDogs[0]);
        }
      })
      .catch(err => {
        console.error('Failed to generate match or fetch matched dog details:', err);
        setError('Failed to generate match. Please try again.');
      });
  };

  // Pagination handlers
  const handleNextPage = () => {
    setPageFrom(pageFrom + pageSize);
  };

  const handlePrevPage = () => {
    setPageFrom(Math.max(0, pageFrom - pageSize));
  };

  return (
    <div style={containerStyle}>
      <h1 style={headerStyle}>Search Dogs by Breed</h1>
      <div style={{ marginBottom: '1rem' }}>
        <label>
          <strong>Select Breed: </strong>
          <select value={breed} onChange={e => { setBreed(e.target.value); setPageFrom(0); }}>
            <option value="">Select a breed</option>
            {breeds.map((b, index) => (
              <option key={index} value={b}>{b}</option>
            ))}
          </select>
        </label>
        <label style={{ marginLeft: '1rem' }}>
          <strong>Sort by Breed: </strong>
          <select value={sortOrder} onChange={e => { setSortOrder(e.target.value); setPageFrom(0); }}>
            <option value="asc">Ascending</option>
            <option value="desc">Descending</option>
          </select>
        </label>
      </div>
      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>Error: {error}</p>}
      {(!loading && dogDetails.length > 0) && (
        <>
          <p>Total Results: {total}</p>
          <div style={cardsContainerStyle}>
            {dogDetails.map((dog) => (
              <div key={dog.id} style={cardStyle}>
                {dog.img && <img src={dog.img} alt={dog.name} style={cardImageStyle} />}
                <p><strong>Name:</strong> {dog.name}</p>
                <p><strong>Breed:</strong> {dog.breed}</p>
                <p><strong>Age:</strong> {dog.age}</p>
                <p><strong>ZIP Code:</strong> {dog.zip_code}</p>
                <label>
                  <input
                    type="checkbox"
                    checked={favorites.includes(dog.id)}
                    onChange={() => toggleFavorite(dog.id)}
                  />{' '}
                  Favorite
                </label>
              </div>
            ))}
          </div>
          <div style={{ textAlign: 'center', margin: '1rem' }}>
            <button style={buttonStyle} onClick={handlePrevPage} disabled={pageFrom === 0}>Previous</button>
            <button style={buttonStyle} onClick={handleNextPage} disabled={!nextQuery && (pageFrom + pageSize >= total)}>Next</button>
          </div>
        </>
      )}
      <div style={{ marginTop: '2rem', textAlign: 'center' }}>
        <button style={{ ...buttonStyle, padding: '1rem 2rem', fontSize: '1rem' }} onClick={handleGenerateMatch}>
          Generate Match from Favorites
        </button>
        {matchResult && !matchedDog && (
          <div style={{ marginTop: '1rem' }}>
            <h2>Your Match</h2>
            <p>Matched Dog ID: {matchResult}</p>
            <p>Loading matched dog details...</p>
          </div>
        )}
        {matchedDog && (
          <div style={{ marginTop: '1rem', border: '2px solid #000', padding: '1rem', borderRadius: '8px', display: 'inline-block' }}>
            <h2>Your Match</h2>
            {matchedDog.img && <img src={matchedDog.img} alt={matchedDog.name} style={{ maxWidth: '200px', borderRadius: '4px' }} />}
            <p><strong>Name:</strong> {matchedDog.name}</p>
            <p><strong>Breed:</strong> {matchedDog.breed}</p>
            <p><strong>Age:</strong> {matchedDog.age}</p>
            <p><strong>ZIP Code:</strong> {matchedDog.zip_code}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default Search;
