import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Search() {
  const [breed, setBreed] = useState('Shihtzu'); // Default breed
  const [dogs, setDogs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [sortOrder, setSortOrder] = useState('asc'); // Sorting order
  const [page, setPage] = useState(0); // Pagination

  useEffect(() => {
    const fetchDogs = () => {
      setLoading(true);
      setError(null);

      axios.get('https://frontend-take-home-service.fetch.com/dogs/search', {
        params: {
          breed,
          size: 25, // Number of results per page
          from: page * 25, // Calculate offset based on current page
          sort: `breed:${sortOrder}` // Sorting order
        }
      })
      .then(response => {
        setDogs(response.data.resultIds || []); // Assuming `resultIds` contains dog objects
        setLoading(false);
      })
      .catch(error => {
        console.error('Failed to fetch dogs:', error);
        setError('Failed to fetch dogs');
        setLoading(false);
      });
    };

    fetchDogs();
  }, [breed, page, sortOrder]); // Effect runs on changes in breed, page, or sort order

  return (
    <div>
      <h1>Dog Search</h1>
      <div>
        <label>
          Breed:
          <input
            type="text"
            value={breed}
            onChange={e => setBreed(e.target.value)}
          />
        </label>
        <label>
          Sort Order:
          <select value={sortOrder} onChange={e => setSortOrder(e.target.value)}>
            <option value="asc">Ascending</option>
            <option value="desc">Descending</option>
          </select>
        </label>
      </div>
      <div>
        <button onClick={() => setPage(prev => prev + 1)}>Next Page</button>
        <button onClick={() => setPage(prev => Math.max(prev - 1, 0))}>Previous Page</button>
      </div>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <ul>
          {dogs.map(dog => (
            <li key={dog.id}>{dog.name} - {dog.breed} - Age: {dog.age}</li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Search;
