import axios from 'axios';

const API_BASE_URL = 'https://frontend-take-home-service.fetch.com';

axios.defaults.withCredentials = true; // Ensure credentials are sent with requests

// Function to fetch details for a single dog by ID
const fetchDogDetails = async (id) => {
    try {
        const response = await axios.get(`${API_BASE_URL}/dogs/${id}`);
        return response.data; // Assuming the endpoint returns the full dog details
    } catch (error) {
        console.error(`Failed to fetch details for dog ID ${id}:`, error);
        return null; // Return null or an empty object if the fetch fails
    }
};

// Function to search dogs and fetch detailed data for each dog
export const searchDogs = async (filters = {}) => {
    try {
        const searchResponse = await axios.get(`${API_BASE_URL}/dogs/search`, { params: filters });
        if (searchResponse.data && Array.isArray(searchResponse.data.resultIds)) {
            const dogDetailsPromises = searchResponse.data.resultIds.map(id => fetchDogDetails(id));
            const dogs = await Promise.all(dogDetailsPromises);
            return dogs.filter(dog => dog !== null); // Filter out any null responses
        }
        return []; // Return empty array if no valid data is returned
    } catch (error) {
        console.error('Search API error:', error);
        return []; // Return empty array on error
    }
};
