import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Search() {
    const [breed, setBreed] = useState('Shih-Tzu'); // Assuming you might want to allow users to search by breed
    const [dogIds, setDogIds] = useState([]);
    const [dogDetails, setDogDetails] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        setLoading(true);
        setError(null);

        axios.get('https://frontend-take-home-service.fetch.com/dogs/search', {
            params: { breed, size: 25, from: 0, sort: 'breed:asc' }
        })
        .then(response => {
            setLoading(false);
            setDogIds(response.data.resultIds);
        })
        .catch(error => {
            console.error('Failed to fetch dog IDs:', error);
            setError('Failed to fetch dog IDs');
            setLoading(false);
        });
    }, [breed]);

    useEffect(() => {
        if (dogIds.length > 0) {
            const fetchDetails = async () => {
                setLoading(true);
                const detailsPromises = dogIds.map(id => 
                    axios.get(`https://frontend-take-home-service.fetch.com/dogs/${id}`)
                );
                try {
                    const detailsResponses = await Promise.all(detailsPromises);
                    setDogDetails(detailsResponses.map(response => response.data));
                    setLoading(false);
                } catch (error) {
                    console.error('Failed to fetch dog details:', error);
                    setError('Failed to fetch dog details');
                    setLoading(false);
                }
            };

            fetchDetails();
        }
    }, [dogIds]); // This runs whenever dogIds updates, fetching details for each new ID

    return (
        <div>
            <h1>Search Dogs by Breed: {breed}</h1>
            {loading && <p>Loading...</p>}
            {error && <p>Error: {error}</p>}
            <ul>
                {dogDetails.map((dog, index) => (
                    <li key={index}>
                        Name: {dog.name}, Breed: {dog.breed}, Age: {dog.age}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Search;
